import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Formik, Form, FormikHelpers } from 'formik';
import * as Yup from 'yup';
import FormField from '../components/FormField';
import Button from '../components/Button';
import { productApi, aiApi } from '../services/api';
import { FormStep, FormValues, Product, Question } from '../types';

const FormContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  background-color: white;
  border-radius: ${({ theme }) => theme.borderRadius.default};
  box-shadow: ${({ theme }) => theme.shadows.medium};

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: 1.5rem;
  }
`;

const FormHeader = styled.div`
  margin-bottom: 2rem;
  text-align: center;
`;

const FormTitle = styled.h1`
  font-size: 2rem;
  color: ${({ theme }) => theme.colors.primary};
  margin-bottom: 1rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1.75rem;
  }
`;

const FormDescription = styled.p`
  color: ${({ theme }) => theme.colors.lightText};
  font-size: 1.125rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1rem;
  }
`;

const FormActions = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 2rem;
`;

const LoadingOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  color: white;
`;

const Spinner = styled.div`
  border: 4px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  border-top: 4px solid white;
  width: 40px;
  height: 40px;
  margin-bottom: 1rem;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const basicFields: Question[] = [
  {
    id: 'name',
    question_text: 'Product Name',
    question_type: 'text',
    required: true,
  },
  {
    id: 'description',
    question_text: 'Product Description',
    question_type: 'text',
    required: true,
    tooltip: 'Provide a detailed description of your product, including its purpose and benefits.',
  },
  {
    id: 'category',
    question_text: 'Product Category',
    question_type: 'select',
    required: true,
    options: [
      { value: 'supplements', label: 'Supplements' },
      { value: 'food', label: 'Food & Beverage' },
      { value: 'cosmetics', label: 'Cosmetics' },
      { value: 'personal_care', label: 'Personal Care' },
      { value: 'household', label: 'Household Products' },
      { value: 'other', label: 'Other' },
    ],
  },
];

const initialBasicValues: FormValues = {
  name: '',
  description: '',
  category: '',
};

const basicValidation = Yup.object({
  name: Yup.string().required('Product name is required'),
  description: Yup.string().required('Product description is required'),
  category: Yup.string().required('Product category is required'),
});

const ProductForm: React.FC = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('');

  const handleBasicSubmit = async (
    values: FormValues,
    { setSubmitting }: FormikHelpers<FormValues>
  ) => {
    setIsLoading(true);
    setLoadingMessage('Submitting your product information...');

    try {
      // Create a product object with only the basic info
      const product: Product = {
        name: values.name,
        description: values.description,
        category: values.category,
        ingredients: '',
        manufacturing_location: '',
        certifications: [],
        additional_info: {},
      };

      // Submit the product directly without generating questions
      const response = await productApi.createProduct(product);

      if (response.success && response.data) {
        setLoadingMessage('Generating transparency report...');

        // Navigate to the report page
        navigate(`/report/${response.data.id}`);
      } else {
        alert(`Error: ${response.error || 'Failed to submit product. Please try again.'}`);
      }
    } catch (error) {
      console.error(error);
      alert('An error occurred while submitting the product.');
    } finally {
      setIsLoading(false);
      setSubmitting(false);
    }
  };

  const handleDynamicSubmit = async (
    values: FormValues,
    { setSubmitting }: FormikHelpers<FormValues>
  ) => {
    setIsLoading(true);
    setLoadingMessage('Submitting your product information...');
    try {
      // Create a product object with basic info
      const product: Product = {
        name: basicInfo?.name || '',
        description: basicInfo?.description || '',
        category: basicInfo?.category || '',
        ingredients: '', // Empty string for ingredients initially
        manufacturing_location: '',
        certifications: [],
        additional_info: {},
      };

      // Extract ingredients from answers if any question is about ingredients
      const ingredientQuestions = dynamicQuestions.filter(q => 
        q.question_text.toLowerCase().includes('ingredient'));

      if (ingredientQuestions.length > 0) {
        const ingredientAnswers = ingredientQuestions.map(q => {
          const key = q.id || q.question_text;
          return values[key];
        }).filter(Boolean);

        if (ingredientAnswers.length > 0) {
          // Join all ingredient answers into a comma-separated string
          product.ingredients = ingredientAnswers.join(', ');
        }
      }

      // Create an array of questions with answers for the backend
      const questions = dynamicQuestions.map(q => {
        const key = q.id || q.question_text;
        return {
          question_text: q.question_text,
          answer: values[key],
          question_type: q.question_type,
          is_ai_generated: true
        };
      });

      // Also store answers in additional_info for compatibility
      dynamicQuestions.forEach((q) => {
        const key = q.id || q.question_text;
        product.additional_info![key] = values[key];
      });

      // Add questions to the product object
      const productWithQuestions = {
        ...product,
        questions: questions
      };

      const response = await productApi.createProduct(productWithQuestions);
      if (response.success && response.data) {
        setLoadingMessage('Generating transparency report...');

        // Navigate to the report page
        navigate(`/report/${response.data.id}`);
      } else {
        alert(`Error: ${response.error || 'Failed to submit product. Please try again.'}`);
      }
    } catch (error) {
      console.error(error);
      alert('An unexpected error occurred. Please try again.');
    } finally {
      setIsLoading(false);
      setSubmitting(false);
    }
  };

  const dynamicValidation = useMemo(() => {
    const shape: Record<string, any> = {};
    dynamicQuestions.forEach((q) => {
      shape[q.id || q.question_text] = q.required
        ? Yup.string().required('This field is required')
        : Yup.string();
    });
    return Yup.object(shape);
  }, [dynamicQuestions]);

  return (
    <div className="container">
      <FormContainer>
        <FormHeader>
          <FormTitle>Product Submission Form</FormTitle>
          <FormDescription>
            Complete the form below to generate a transparency report for your product.
          </FormDescription>
        </FormHeader>

        {step === 1 && (
          <Formik
            initialValues={initialBasicValues}
            validationSchema={basicValidation}
            onSubmit={handleBasicSubmit}
            validateOnChange={false}
            validateOnBlur={true}
          >
            {({ isSubmitting }) => (
              <Form>
                {basicFields.map((field) => (
                  <FormField
                    key={field.id}
                    label={field.question_text}
                    name={field.id!}
                    type={field.question_type === 'select' ? 'select' : 'text'}
                    options={
                      Array.isArray(field.options)
                        ? field.options.map((option) =>
                            typeof option === 'string'
                              ? { value: option, label: option }
                              : option
                          )
                        : undefined
                    }
                    tooltip={field.tooltip}
                    required={field.required}
                  />
                ))}
                <FormActions>
                  <Button type="submit" disabled={isSubmitting} isLoading={isSubmitting}>
                    Next
                  </Button>
                </FormActions>
              </Form>
            )}
          </Formik>
        )}

        {step === 2 && (
          <Formik
            initialValues={dynamicQuestions.reduce((acc, q) => {
              acc[q.id || q.question_text] = '';
              return acc;
            }, {} as FormValues)}
            validationSchema={dynamicValidation}
            onSubmit={handleDynamicSubmit}
            validateOnChange={false}
            validateOnBlur={true}
          >
            {({ isSubmitting }) => (
              <Form>
                {dynamicQuestions.map((field) => (
                  <FormField
                    key={field.id || field.question_text}
                    label={field.question_text}
                    name={field.id || field.question_text}
                    type={field.question_type === 'select' ? 'select' : 'text'}
                    options={
                      Array.isArray(field.options)
                        ? field.options.map((option) =>
                            typeof option === 'string'
                              ? { value: option, label: option }
                              : option
                          )
                        : undefined
                    }
                    tooltip={field.tooltip}
                    required={field.required}
                  />
                ))}
                <FormActions>
                  <Button type="submit" disabled={isSubmitting} isLoading={isSubmitting}>
                    Submit
                  </Button>
                </FormActions>
              </Form>
            )}
          </Formik>
        )}

        {isLoading && (
          <LoadingOverlay>
            <Spinner />
            <p>{loadingMessage}</p>
          </LoadingOverlay>
        )}
      </FormContainer>
    </div>
  );
};

export default ProductForm;
