import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import reportService, { Report, GenerateReportParams } from '../services/reportService';
import { useAuth } from './AuthContext';

// Context interface
interface ReportContextType {
  reports: Report[];
  userReports: Report[];
  isLoading: boolean;
  error: string | null;
  generateReport: (params: GenerateReportParams) => Promise<Report>;
  getReportsByProduct: (productId: string) => Promise<Report[]>;
  getReportById: (reportId: string) => Promise<Report>;
  deleteReport: (reportId: string) => Promise<void>;
  refreshReports: () => Promise<void>;
  clearError: () => void;
}

// Create context with default values
const ReportContext = createContext<ReportContextType>({
  reports: [],
  userReports: [],
  isLoading: false,
  error: null,
  generateReport: async () => ({ id: '', product: '', filename: '', filepath: '', created_by: '', report_type: '', created_at: new Date(), metadata: {} }),
  getReportsByProduct: async () => [],
  getReportById: async () => ({ id: '', product: '', filename: '', filepath: '', created_by: '', report_type: '', created_at: new Date(), metadata: {} }),
  deleteReport: async () => {},
  refreshReports: async () => {},
  clearError: () => {},
});

// Props interface
interface ReportProviderProps {
  children: ReactNode;
}

/**
 * Report Provider Component
 * Provides report state and methods to the app
 */
export const ReportProvider: React.FC<ReportProviderProps> = ({ children }) => {
  const [reports, setReports] = useState<Report[]>([]);
  const [userReports, setUserReports] = useState<Report[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const { user, isAdmin, isAuthenticated } = useAuth();

  // Load reports when user changes
  useEffect(() => {
    if (isAuthenticated) {
      refreshReports();
    }
  }, [isAuthenticated, user?.id]);

  /**
   * Refresh reports data
   */
  const refreshReports = async () => {
    if (!isAuthenticated) return;

    setIsLoading(true);
    setError(null);

    try {
      // Get user reports
      const userReportsData = await reportService.getReportsByUser();
      setUserReports(userReportsData);

      // Get all reports for admin users
      if (isAdmin) {
        const allReports = await reportService.getAllReports();
        setReports(allReports);
      } else {
        setReports(userReportsData);
      }
    } catch (err: any) {
      setError(err.message || 'Failed to load reports');
      console.error('Error loading reports:', err);
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * Generate a new report
   * @param params Report generation parameters
   */
  const generateReport = async (params: GenerateReportParams) => {
    setIsLoading(true);
    setError(null);

    try {
      const newReport = await reportService.generateReport(params);
      await refreshReports();
      return newReport;
    } catch (err: any) {
      setError(err.message || 'Failed to generate report');
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * Get reports by product ID
   * @param productId Product ID
   */
  const getReportsByProduct = async (productId: string) => {
    setIsLoading(true);
    setError(null);

    try {
      return await reportService.getReportsByProduct(productId);
    } catch (err: any) {
      setError(err.message || 'Failed to get reports');
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * Get report by ID
   * @param reportId Report ID
   */
  const getReportById = async (reportId: string) => {
    setIsLoading(true);
    setError(null);

    try {
      return await reportService.getReportById(reportId);
    } catch (err: any) {
      setError(err.message || 'Failed to get report');
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * Delete a report
   * @param reportId Report ID
   */
  const deleteReport = async (reportId: string) => {
    setIsLoading(true);
    setError(null);

    try {
      await reportService.deleteReport(reportId);
      await refreshReports();
    } catch (err: any) {
      setError(err.message || 'Failed to delete report');
      throw err;
    } finally {
      setIsLoading(false);
    }
  };

  /**
   * Clear error
   */
  const clearError = () => {
    setError(null);
  };

  // Context value
  const value = {
    reports,
    userReports,
    isLoading,
    error,
    generateReport,
    getReportsByProduct,
    getReportById,
    deleteReport,
    refreshReports,
    clearError,
  };

  return <ReportContext.Provider value={value}>{children}</ReportContext.Provider>;
};

/**
 * Custom hook to use report context
 * @returns Report context
 */
export const useReports = () => useContext(ReportContext);
