import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Formik, Form, FormikHelpers } from 'formik';
import * as Yup from 'yup';
import FormField from '../components/FormField';
import Button from '../components/Button';
import { productApi, aiApi } from '../services/api';
import { FormStep, FormValues, Product, Question, GenerateQuestionsRequest } from '../types';

const FormContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  background-color: white;
  border-radius: ${({ theme }) => theme.borderRadius.default};
  box-shadow: ${({ theme }) => theme.shadows.medium};

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: 1.5rem;
  }
`;

const FormHeader = styled.div`
  margin-bottom: 2rem;
  text-align: center;
`;

const FormTitle = styled.h1`
  font-size: 2rem;
  color: ${({ theme }) => theme.colors.primary};
  margin-bottom: 1rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1.75rem;
  }
`;

const FormDescription = styled.p`
  color: ${({ theme }) => theme.colors.lightText};
  font-size: 1.125rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1rem;
  }
`;

const FormActions = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 2rem;
`;

const LoadingOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  color: white;
`;

const Spinner = styled.div`
  border: 4px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  border-top: 4px solid white;
  width: 40px;
  height: 40px;
  margin-bottom: 1rem;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const basicFields: Question[] = [
  {
    id: 'name',
    question_text: 'Product Name',
    question_type: 'text',
    required: true,
  },
  {
    id: 'description',
    question_text: 'Product Description',
    question_type: 'text',
    required: true,
    tooltip: 'Provide a detailed description of your product, including its purpose and benefits.',
  },
  {
    id: 'category',
    question_text: 'Product Category',
    question_type: 'select',
    required: true,
    options: [
      { value: 'supplements', label: 'Supplements' },
      { value: 'food', label: 'Food & Beverage' },
      { value: 'cosmetics', label: 'Cosmetics' },
      { value: 'personal_care', label: 'Personal Care' },
      { value: 'household', label: 'Household Products' },
      { value: 'other', label: 'Other' },
    ],
  },
];

const initialBasicValues: FormValues = {
  name: '',
  description: '',
  category: '',
};

const basicValidation = Yup.object({
  name: Yup.string().required('Product name is required'),
  description: Yup.string().required('Product description is required'),
  category: Yup.string().required('Product category is required'),
});

const ProductForm: React.FC = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('');
  const [currentStep, setCurrentStep] = useState(1);
  const [productData, setProductData] = useState<Product | null>(null);
  const [aiQuestions, setAiQuestions] = useState<Question[]>([]);

  const handleBasicSubmit = async (
    values: FormValues,
    { setSubmitting }: FormikHelpers<FormValues>
  ) => {
    setIsLoading(true);
    setLoadingMessage('Generating AI questions...');

    try {
      // Create a product object with only the basic info
      const product: Product = {
        name: values.name,
        description: values.description,
        category: values.category,
        ingredients: '',
        manufacturing_location: '',
        certifications: [],
        additional_info: {},
      };

      // Save product data for later submission
      setProductData(product);

      // Generate AI questions based on product information
      const generateRequest: GenerateQuestionsRequest = {
        product: product,
        num_questions: 5 // You can adjust this number as needed
      };

      const aiResponse = await aiApi.generateQuestions(generateRequest);

      if (aiResponse.success && aiResponse.data) {
        // Convert the AI-generated questions to Question objects
        const questions: Question[] = aiResponse.data.questions.map((questionText, index) => ({
          id: `q_${index}`,
          question_text: questionText,
          question_type: 'text' as const,
          required: false,
          is_ai_generated: true
        }));

        setAiQuestions(questions);
        setCurrentStep(2); // Move to the AI questions step
      } else {
        alert(`Error: ${aiResponse.error || 'Failed to generate questions. Please try again.'}`);
      }
    } catch (error) {
      console.error(error);
      alert('An error occurred while generating questions.');
    } finally {
      setIsLoading(false);
      setSubmitting(false);
    }
  };

  // Handle the submission of AI-generated questions
  const handleQuestionsSubmit = async (
    values: FormValues,
    { setSubmitting }: FormikHelpers<FormValues>
  ) => {
    if (!productData) {
      alert('Product data is missing. Please go back and try again.');
      return;
    }

    setIsLoading(true);
    setLoadingMessage('Submitting your product information...');

    try {
      // Create a product with the answers to AI questions
      const product: Product = {
        ...productData,
        additional_info: values // Store all question answers in additional_info
      };

      // Submit the product
      const response = await productApi.createProduct(product);

      if (response.success && response.data) {
        setLoadingMessage('Calculating transparency score...');

        // Prepare data for transparency score calculation
        const productInfo = {
          name: product.name,
          description: product.description,
          category: product.category,
          ingredients: product.ingredients || '',
          manufacturing_location: product.manufacturing_location || '',
          certifications: product.certifications || []
        };

        // Convert question answers to the format expected by the AI service
        const answers: Record<string, string> = {};
        aiQuestions.forEach(question => {
          if (question.id && values[question.id]) {
            answers[question.question_text] = values[question.id];
          }
        });

        // Calculate transparency score
        const scoreResponse = await aiApi.getTransparencyScore({
          product: productInfo,
          answers: answers
        });

        if (scoreResponse.success && scoreResponse.data) {
          setLoadingMessage('Generating transparency report...');

          // Navigate to the report page
          navigate(`/report/${response.data.id}`);
        } else {
          console.error('Failed to calculate transparency score:', scoreResponse.error);
          // Still navigate to report page even if score calculation fails
          navigate(`/report/${response.data.id}`);
        }
      } else {
        alert(`Error: ${response.error || 'Failed to submit product. Please try again.'}`);
      }
    } catch (error) {
      console.error(error);
      alert('An error occurred while submitting the product.');
    } finally {
      setIsLoading(false);
      setSubmitting(false);
    }
  };

  // Generate initial values for AI questions form
  const initialQuestionsValues = useMemo(() => {
    const values: FormValues = {};
    aiQuestions.forEach(question => {
      values[question.id!] = '';
    });
    return values;
  }, [aiQuestions]);

  return (
    <div className="container">
      <FormContainer>
        <FormHeader>
          <FormTitle>Product Submission Form</FormTitle>
          <FormDescription>
            {currentStep === 1 
              ? 'Complete the form below to generate AI questions about your product.'
              : 'Please answer the following AI-generated questions about your product.'}
          </FormDescription>
        </FormHeader>

        {currentStep === 1 ? (
          <Formik
            initialValues={initialBasicValues}
            validationSchema={basicValidation}
            onSubmit={handleBasicSubmit}
            validateOnChange={false}
            validateOnBlur={true}
          >
            {({ isSubmitting }) => (
              <Form>
                {basicFields.map((field) => (
                  <FormField
                    key={field.id}
                    label={field.question_text}
                    name={field.id!}
                    type={field.question_type === 'select' ? 'select' : 'text'}
                    options={
                      Array.isArray(field.options)
                        ? field.options.map((option) =>
                            typeof option === 'string'
                              ? { value: option, label: option }
                              : option
                          )
                        : undefined
                    }
                    tooltip={field.tooltip}
                    required={field.required}
                  />
                ))}
                <FormActions>
                  <Button type="submit" disabled={isSubmitting} isLoading={isSubmitting}>
                    Next
                  </Button>
                </FormActions>
              </Form>
            )}
          </Formik>
        ) : (
          <Formik
            initialValues={initialQuestionsValues}
            onSubmit={handleQuestionsSubmit}
            validateOnChange={false}
            validateOnBlur={true}
          >
            {({ isSubmitting }) => (
              <Form>
                {aiQuestions.map((question) => (
                  <FormField
                    key={question.id}
                    label={question.question_text}
                    name={question.id!}
                    type="textarea"
                    required={question.required}
                  />
                ))}
                <FormActions>
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setCurrentStep(1)}
                    disabled={isSubmitting}
                  >
                    Back
                  </Button>
                  <Button type="submit" disabled={isSubmitting} isLoading={isSubmitting}>
                    Submit
                  </Button>
                </FormActions>
              </Form>
            )}
          </Formik>
        )}

        {isLoading && (
          <LoadingOverlay>
            <Spinner />
            <p>{loadingMessage}</p>
          </LoadingOverlay>
        )}
      </FormContainer>
    </div>
  );
};

export default ProductForm;
