import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import styled from 'styled-components';
import Button from '../components/Button';
import { reportApi, productApi } from '../services/api';
import { Product, Report } from '../types';

const ReportContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  background-color: white;
  border-radius: ${({ theme }) => theme.borderRadius.default};
  box-shadow: ${({ theme }) => theme.shadows.medium};

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: 1.5rem;
  }
`;

const ReportHeader = styled.div`
  margin-bottom: 2rem;
  text-align: center;
  padding-bottom: 1.5rem;
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
`;

const ReportTitle = styled.h1`
  font-size: 2rem;
  color: ${({ theme }) => theme.colors.primary};
  margin-bottom: 1rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1.75rem;
  }
`;

const ReportSubtitle = styled.p`
  color: ${({ theme }) => theme.colors.lightText};
  font-size: 1.125rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1rem;
  }
`;

const ReportSection = styled.section`
  margin-bottom: 2rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.5rem;
  color: ${({ theme }) => theme.colors.primary};
  margin-bottom: 1rem;
  padding-bottom: 0.5rem;
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
`;

const InfoGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1.5rem;
  margin-bottom: 1.5rem;
`;

const InfoItem = styled.div`
  margin-bottom: 1rem;
`;

const InfoLabel = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: ${({ theme }) => theme.colors.text};
`;

const InfoValue = styled.p`
  color: ${({ theme }) => theme.colors.text};
  line-height: 1.6;
`;

const TagsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 0.5rem;
`;

const Tag = styled.span`
  background-color: ${({ theme }) => theme.colors.lightBg};
  color: ${({ theme }) => theme.colors.text};
  padding: 0.25rem 0.75rem;
  border-radius: ${({ theme }) => theme.borderRadius.small};
  font-size: 0.875rem;
`;

const TransparencyScore = styled.div`
  background-color: ${({ theme }) => theme.colors.lightBg};
  border-radius: ${({ theme }) => theme.borderRadius.default};
  padding: 1.5rem;
  margin-bottom: 2rem;
  text-align: center;
`;

const ScoreTitle = styled.h3`
  font-size: 1.25rem;
  margin-bottom: 1rem;
  color: ${({ theme }) => theme.colors.primary};
`;

const ScoreValue = styled.div<{ score: number }>`
  font-size: 3rem;
  font-weight: 700;
  color: ${({ score, theme }) => {
    if (score >= 80) return theme.colors.success;
    if (score >= 60) return theme.colors.info;
    if (score >= 40) return theme.colors.warning;
    return theme.colors.error;
  }};
  margin-bottom: 1rem;
`;

const ScoreFeedback = styled.p`
  color: ${({ theme }) => theme.colors.text};
  margin-bottom: 1rem;
`;

const ScoreDetails = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 1.5rem;
`;

const ScoreDetailItem = styled.div`
  text-align: center;
`;

const ScoreDetailLabel = styled.p`
  font-size: 0.875rem;
  color: ${({ theme }) => theme.colors.lightText};
  margin-bottom: 0.5rem;
`;

const ScoreDetailValue = styled.p<{ $positive?: boolean }>`
  font-weight: 600;
  color: ${({ $positive, theme }) => ($positive ? theme.colors.success : theme.colors.error)};
`;

const ReportActions = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 3rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    flex-direction: column;
  }
`;

const PDFPreviewContainer = styled.div`
  margin-top: 2rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.default};
  overflow: hidden;
  height: 600px;
`;

const PDFEmbed = styled.iframe`
  width: 100%;
  height: 100%;
  border: none;
`;

const LoadingContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 400px;
`;

const Spinner = styled.div`
  border: 4px solid rgba(0, 0, 0, 0.1);
  border-radius: 50%;
  border-top: 4px solid ${({ theme }) => theme.colors.primary};
  width: 40px;
  height: 40px;
  margin-bottom: 1rem;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const ErrorContainer = styled.div`
  text-align: center;
  padding: 3rem;
`;

const ErrorTitle = styled.h2`
  font-size: 1.5rem;
  color: ${({ theme }) => theme.colors.error};
  margin-bottom: 1rem;
`;

const ErrorMessage = styled.p`
  color: ${({ theme }) => theme.colors.text};
  margin-bottom: 2rem;
`;

const ReportPreview: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [report, setReport] = useState<Report | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      if (!id) {
        setError('Invalid report ID');
        setLoading(false);
        return;
      }

      try {
        // Fetch product data
        const productResponse = await productApi.getProduct(id);
        if (!productResponse.success || !productResponse.data) {
          throw new Error(productResponse.error || 'Failed to fetch product data');
        }
        setProduct(productResponse.data);

        // Fetch report data
        const reportResponse = await reportApi.getReport(id);
        if (!reportResponse.success || !reportResponse.data) {
          throw new Error(reportResponse.error || 'Failed to fetch report data');
        }
        setReport(reportResponse.data);
      } catch (err: any) {
        setError(err.message || 'An error occurred while fetching the report');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const handleDownloadReport = async () => {
    if (!id) return;

    try {
      const blob = await reportApi.downloadReport(id);
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `transparency-report-${id}.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (err: any) {
      alert(`Error downloading report: ${err.message}`);
    }
  };

  if (loading) {
    return (
      <ReportContainer>
        <LoadingContainer>
          <Spinner />
          <p>Loading report...</p>
        </LoadingContainer>
      </ReportContainer>
    );
  }

  if (error || !product) {
    return (
      <ReportContainer>
        <ErrorContainer>
          <ErrorTitle>Error Loading Report</ErrorTitle>
          <ErrorMessage>{error || 'Product not found'}</ErrorMessage>
          <Button as={Link} to="/submit" variant="primary">
            Submit a New Product
          </Button>
        </ErrorContainer>
      </ReportContainer>
    );
  }

  // Use the transparency score from the report or fallback to default values
  const transparencyScore = {
    score: report?.transparency_score || 70,
    feedback: report?.feedback || 'Your product demonstrates good transparency with room for improvement.',
    strengths: report?.strengths || ['Product information provided'],
    areas_to_improve: report?.areas_for_improvement || ['Consider adding more details'],
  };

  return (
    <div className="container">
      <ReportContainer>
        <ReportHeader>
          <ReportTitle>Transparency Report</ReportTitle>
          <ReportSubtitle>{product.name}</ReportSubtitle>
        </ReportHeader>

        <TransparencyScore>
          <ScoreTitle>Transparency Score</ScoreTitle>
          <ScoreValue score={transparencyScore.score}>{transparencyScore.score}%</ScoreValue>
          <ScoreFeedback>{transparencyScore.feedback}</ScoreFeedback>
          <ScoreDetails>
            <ScoreDetailItem>
              <ScoreDetailLabel>Strengths</ScoreDetailLabel>
              <ScoreDetailValue $positive>{transparencyScore.strengths.length}</ScoreDetailValue>
            </ScoreDetailItem>
            <ScoreDetailItem>
              <ScoreDetailLabel>Areas to Improve</ScoreDetailLabel>
              <ScoreDetailValue>{transparencyScore.areas_to_improve.length}</ScoreDetailValue>
            </ScoreDetailItem>
          </ScoreDetails>
        </TransparencyScore>

        <ReportSection>
          <SectionTitle>Product Information</SectionTitle>
          <InfoGrid>
            <InfoItem>
              <InfoLabel>Product Name</InfoLabel>
              <InfoValue>{product.name}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Category</InfoLabel>
              <InfoValue>{product.category}</InfoValue>
            </InfoItem>
          </InfoGrid>
          <InfoItem>
            <InfoLabel>Description</InfoLabel>
            <InfoValue>{product.description}</InfoValue>
          </InfoItem>
        </ReportSection>

        <ReportSection>
          <SectionTitle>Manufacturing Information</SectionTitle>
          {product.ingredients && product.ingredients.length > 0 && (
            <InfoItem>
              <InfoLabel>Ingredients</InfoLabel>
              <TagsContainer>
                {Array.isArray(product.ingredients) 
                  ? product.ingredients.map((ingredient: string, index: number) => (
                      <Tag key={index}>{ingredient}</Tag>
                    ))
                  : product.ingredients.split(',').map((ingredient: string, index: number) => (
                      <Tag key={index}>{ingredient.trim()}</Tag>
                    ))
                }
              </TagsContainer>
            </InfoItem>
          )}
          <InfoGrid>
            <InfoItem>
              <InfoLabel>Manufacturing Location</InfoLabel>
              <InfoValue>{product.manufacturing_location}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Certifications</InfoLabel>
              {product.certifications && product.certifications.length > 0 ? (
                <TagsContainer>
                  {product.certifications.map((cert: string, index: number) => (
                    <Tag key={index}>{cert}</Tag>
                  ))}
                </TagsContainer>
              ) : (
                <InfoValue>None</InfoValue>
              )}
            </InfoItem>
          </InfoGrid>
        </ReportSection>

        {product.additional_info && Object.keys(product.additional_info).length > 0 && (
          <ReportSection>
            <SectionTitle>Additional Information</SectionTitle>
            {Object.entries(product.additional_info).map(([key, value]: [string, any], index: number) => (
              <InfoItem key={key}>
                <InfoLabel>{key.replace(/_/g, ' ').replace(/\b\w/g, (l: string) => l.toUpperCase())}</InfoLabel>
                <InfoValue>{value}</InfoValue>
              </InfoItem>
            ))}
          </ReportSection>
        )}

        <ReportSection>
          <SectionTitle>Transparency Analysis</SectionTitle>
          <InfoItem>
            <InfoLabel>Strengths</InfoLabel>
            <ul>
              {transparencyScore.strengths.map((strength: string, index: number) => (
                <li key={index}>{strength}</li>
              ))}
            </ul>
          </InfoItem>
          <InfoItem>
            <InfoLabel>Areas to Improve</InfoLabel>
            <ul>
              {transparencyScore.areas_to_improve.map((area: string, index: number) => (
                <li key={index}>{area}</li>
              ))}
            </ul>
          </InfoItem>
        </ReportSection>

        {report && report.pdf_url && (
          <PDFPreviewContainer>
            <PDFEmbed src={`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${report.pdf_url}`} title="PDF Preview" />
          </PDFPreviewContainer>
        )}

        <ReportActions>
          <Button variant="outline" as={Link} to="/submit">
            Submit Another Product
          </Button>
          <Button onClick={handleDownloadReport}>
            Download PDF Report
          </Button>
        </ReportActions>
      </ReportContainer>
    </div>
  );
};

export default ReportPreview;
