    description: 'Tell us about what goes into your product and how it\'s made.',
    fields: [
      {
        id: 'ingredients',
        question_text: 'Ingredients',
        question_type: 'text',
        required: true,
        tooltip: 'List all ingredients in your product. Separate each ingredient with a comma.',
      },
      {
