// Validation schema for each step
const validationSchemas = [
  // Step 1: Basic Information
  Yup.object({
    name: Yup.string().required('Product name is required'),
    description: Yup.string().required('Product description is required'),
    category: Yup.string().required('Product category is required'),
  }),
  // Step 2: Ingredients & Manufacturing
  Yup.object({
    ingredients: Yup.string().required('Ingredients are required'),
    manufacturing_location: Yup.string().required('Manufacturing location is required'),
    manufacturing_process: Yup.string().required('Manufacturing process is required'),
    country_of_origin: Yup.string().required('Country of origin is required'),
    certifications: Yup.string(),
  }),
  // Step 3: Additional Questions (dynamic validation will be added)
  Yup.object({}),
  // Step 4: Review & Submit
  Yup.object({}),
];

const ProductForm: React.FC = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(0);
  const [steps, setSteps] = useState<FormStep[]>(initialSteps);
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('');

  // Initial form values
  const initialValues: FormValues = {
    name: '',
    description: '',
    category: '',
    ingredients: '',
    manufacturing_location: '',
    manufacturing_process: '',
    country_of_origin: '',
    certifications: '',
  };

  // Generate additional questions based on product information
  const generateAdditionalQuestions = async (values: FormValues) => {
    setIsLoading(true);
    setLoadingMessage('Generating intelligent follow-up questions...');

    try {
      // Parse ingredients and certifications from comma-separated strings to arrays
      const ingredientsArray = values.ingredients
        ? values.ingredients.split(',').map((item: string) => item.trim())
        : [];

      const certificationsArray = values.certifications
        ? values.certifications.split(',').map((item: string) => item.trim())
        : [];

      // Create product object for AI service
      const product: Product = {
        name: values.name,
        description: values.description,
        category: values.category,
        ingredients: ingredientsArray,
        manufacturing_location: values.manufacturing_location,
        certifications: certificationsArray,
      };

      // Call AI service to generate questions
      const response = await aiApi.generateQuestions({ product });

      if (response.success && response.data) {
        // Update the additional questions step with generated questions
        const updatedSteps = [...steps];
        updatedSteps[2].fields = response.data.questions;

        // Update validation schema for additional questions
        const additionalQuestionsSchema: { [key: string]: any } = {};
        response.data.questions.forEach((question) => {
          if (question.required) {
            additionalQuestionsSchema[question.id || ''] = Yup.string().required(`${question.question_text} is required`);
          }
        });

        validationSchemas[2] = Yup.object(additionalQuestionsSchema);

        setSteps(updatedSteps);
      } else {
        // If AI service fails, use some default questions
        const defaultQuestions: Question[] = [
          {
            id: 'sourcing',
            question_text: 'How are your ingredients sourced?',
            question_type: 'text',
            required: true,
          },
          {
            id: 'testing',
            question_text: 'What testing procedures do you use to ensure product safety?',
            question_type: 'text',
            required: true,
          },
          {
            id: 'packaging',
            question_text: 'Describe your packaging materials and sustainability practices.',
            question_type: 'text',
            required: true,
          },
        ];

        const updatedSteps = [...steps];
        updatedSteps[2].fields = defaultQuestions;

        // Update validation schema for default questions
        validationSchemas[2] = Yup.object({
          sourcing: Yup.string().required('This field is required'),
          testing: Yup.string().required('This field is required'),
          packaging: Yup.string().required('This field is required'),
        });

        setSteps(updatedSteps);
      }
    } catch (error) {
      console.error('Error generating questions:', error);
      // Use default questions on error
    } finally {
      setIsLoading(false);
    }
  };

  // Handle form submission
  const handleSubmit = async (values: FormValues, { setSubmitting }: FormikHelpers<FormValues>) => {
    if (currentStep < steps.length - 1) {
      setSubmitting(false);

      // If moving from step 2 to step 3, generate additional questions
      if (currentStep === 1) {
        await generateAdditionalQuestions(values);
      }

      setCurrentStep(currentStep + 1);
      return;
    }

    // Final submission
    setIsLoading(true);
    setLoadingMessage('Submitting your product information...');

    try {
      // Parse ingredients and certifications from comma-separated strings to arrays
      const ingredientsArray = values.ingredients
        ? values.ingredients.split(',').map((item: string) => item.trim())
        : [];

      const certificationsArray = values.certifications
        ? values.certifications.split(',').map((item: string) => item.trim())
        : [];

      // Create product object for submission
      const product: Product = {
        name: values.name,
        description: values.description,
        category: values.category,
        ingredients: ingredientsArray,
        manufacturing_location: values.manufacturing_location,
        certifications: certificationsArray,
        additional_info: {},
      };

      // Add answers to additional questions
      steps[2].fields.forEach((field) => {
        if (field.id && values[field.id]) {
          product.additional_info![field.id] = values[field.id];
        }
      });

      // Submit product to API
      const response = await productApi.createProduct(product);

      if (response.success && response.data) {
        // Navigate to report preview page
        navigate(`/report/${response.data.id}`);
      } else {
        // Handle error
        console.error('Error submitting product:', response.error);
        alert(`Error: ${response.error || 'Failed to submit product. Please try again.'}`);
      }
    } catch (error) {
      console.error('Error submitting product:', error);
      alert('An unexpected error occurred. Please try again.');
    } finally {
      setIsLoading(false);
      setSubmitting(false);
    }
  };

  return (
    <div className="container">
      <FormContainer>
        <FormHeader>
          <FormTitle>Product Submission Form</FormTitle>
          <FormDescription>
            Complete the form below to generate a transparency report for your product.
          </FormDescription>
        </FormHeader>

        <StepIndicator>
          {steps.map((step, index) => (
            <Step
              key={step.id}
              $active={currentStep === index}
              $completed={currentStep > index}
            >
              <StepCircle $active={currentStep === index} $completed={currentStep > index}>
                {currentStep > index ? '✓' : index + 1}
              </StepCircle>
              <StepLabel $active={currentStep === index}>{step.title}</StepLabel>
            </Step>
          ))}
        </StepIndicator>

        <Formik
          initialValues={initialValues}
          validationSchema={validationSchemas[currentStep]}
          onSubmit={handleSubmit}
          validateOnChange={false}
          validateOnBlur={true}
        >
          {({ isSubmitting, values }) => (
            <Form>
              <StepContent>
                <StepTitle>{steps[currentStep].title}</StepTitle>
                <StepDescription>{steps[currentStep].description}</StepDescription>

                {currentStep === 3 ? (
                  // Review step - show summary of all inputs
                  <div>
                    <h3>Basic Information</h3>
                    <p><strong>Product Name:</strong> {values.name}</p>
                    <p><strong>Description:</strong> {values.description}</p>
                    <p><strong>Category:</strong> {values.category}</p>

                    <h3>Ingredients & Manufacturing</h3>
                    <p><strong>Ingredients:</strong> {values.ingredients}</p>
                    <p><strong>Manufacturing Location:</strong> {values.manufacturing_location}</p>
                    <p><strong>Certifications:</strong> {values.certifications || 'None'}</p>

                    <h3>Additional Information</h3>
                    {steps[2].fields.map((field) => (
                      <p key={field.id}>
                        <strong>{field.question_text}:</strong> {values[field.id || ''] || 'Not provided'}
                      </p>
                    ))}
                  </div>
                ) : (
                  // Regular form fields for current step
                  steps[currentStep].fields.map((field) => {
                    // Force category field to always render as a select
                    const isCategory = field.id === 'category';
                    return (
                      <FormField
                        key={field.id}
                        label={field.question_text}
                        name={field.id || ''}
                        type={isCategory ? 'select' : field.question_type === 'select' ? 'select' : field.question_type === 'multiselect' ? 'checkbox' : 'text'}
                        as={field.question_type === 'text' && !isCategory ? 'textarea' : undefined}
                        options={field.options?.map((option) => {
                          if (typeof option === 'string') {
                            return { value: option, label: option };
                          }
                          return option;
                        })}
                        tooltip={field.tooltip}
                        required={field.required}
                      />
                    );
                  })
                )}
              </StepContent>

              {/* Debug log for Formik values */}
              {process.env.NODE_ENV === 'development' && (
                <pre style={{ background: '#f8f8f8', padding: '1rem', marginTop: '2rem' }}>
                  {JSON.stringify(values, null, 2)}
                </pre>
              )}

              <FormActions>
                {currentStep > 0 && (
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setCurrentStep(currentStep - 1)}
                    disabled={isSubmitting}
                  >
                    Back
                  </Button>
                )}
                <Button
                  type="submit"
                  disabled={isSubmitting}
                  isLoading={isSubmitting}
                >
                  {currentStep < steps.length - 1 ? 'Next' : 'Submit'}
                </Button>
              </FormActions>
            </Form>
          )}
        </Formik>
      </FormContainer>

      {isLoading && (
        <LoadingOverlay>
          <Spinner />
          <p>{loadingMessage}</p>
        </LoadingOverlay>
      )}
    </div>
  );
};

export default ProductForm;
