import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Formik, Form, FormikHelpers } from 'formik';
import * as Yup from 'yup';
import FormField from '../components/FormField';
import Button from '../components/Button';
import { productApi, aiApi } from '../services/api';
import { Product, Question, FormStep, FormValues } from '../types';

const FormContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  background-color: white;
  border-radius: ${({ theme }) => theme.borderRadius.default};
  box-shadow: ${({ theme }) => theme.shadows.medium};

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    padding: 1.5rem;
  }
`;

const FormHeader = styled.div`
  margin-bottom: 2rem;
  text-align: center;
`;

const FormTitle = styled.h1`
  font-size: 2rem;
  color: ${({ theme }) => theme.colors.primary};
  margin-bottom: 1rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1.75rem;
  }
`;

const FormDescription = styled.p`
  color: ${({ theme }) => theme.colors.lightText};
  font-size: 1.125rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    font-size: 1rem;
  }
`;

const StepIndicator = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 2rem;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 2px;
    background-color: ${({ theme }) => theme.colors.border};
    transform: translateY(-50%);
    z-index: 0;
  }
`;

const Step = styled.div<{ $active: boolean; $completed: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  z-index: 1;
`;

const StepCircle = styled.div<{ $active: boolean; $completed: boolean }>`
  width: 2.5rem;
  height: 2.5rem;
  border-radius: 50%;
  background-color: ${({ $active, $completed, theme }) =>
    $completed
      ? theme.colors.success
      : $active
      ? theme.colors.primary
      : theme.colors.lightBg};
  color: ${({ $active, $completed }) => ($active || $completed ? 'white' : '#666')};
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  margin-bottom: 0.5rem;
  transition: all ${({ theme }) => theme.transitions.default};
  border: 2px solid ${({ $active, $completed, theme }) =>
    $completed
      ? theme.colors.success
      : $active
      ? theme.colors.primary
      : theme.colors.border};
`;

const StepLabel = styled.span<{ $active: boolean }>`
  font-size: 0.875rem;
  color: ${({ $active, theme }) => ($active ? theme.colors.primary : theme.colors.lightText)};
  font-weight: ${({ $active }) => ($active ? '600' : '400')};
  text-align: center;
  max-width: 100px;
`;

const StepContent = styled.div`
  margin-bottom: 2rem;
`;

const StepTitle = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: ${({ theme }) => theme.colors.primary};
`;

const StepDescription = styled.p`
  color: ${({ theme }) => theme.colors.lightText};
  margin-bottom: 2rem;
`;

const FormActions = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 2rem;
`;

const LoadingOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  color: white;
`;

const Spinner = styled.div`
  border: 4px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  border-top: 4px solid white;
  width: 40px;
  height: 40px;
  margin-bottom: 1rem;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

// ... existing code ...
const initialSteps: FormStep[] = [
  {
    id: 'basic-info',
    title: 'Basic Information',
    description: "Let's start with some basic information about your product.",
    fields: [
      {
        id: 'name',
        question_text: 'Product Name',
        question_type: 'text',
        required: true,
      },
      {
        id: 'description',
        question_text: 'Product Description',
        question_type: 'text',
        required: true,
        tooltip: 'Provide a detailed description of your product, including its purpose and benefits.',
      },
      {
        id: 'category',
        question_text: 'Product Category',
        question_type: 'select',
        required: true,
        options: [
          { value: 'supplements', label: 'Supplements' },
          { value: 'food', label: 'Food & Beverage' },
          { value: 'cosmetics', label: 'Cosmetics' },
          { value: 'personal_care', label: 'Personal Care' },
          { value: 'household', label: 'Household Products' },
          { value: 'other', label: 'Other' },
        ],
      },
    ],
  },
];

const initialValues: FormValues = {
  name: '',
  description: '',
  category: '',
};

const validationSchemas = [
  Yup.object({
    name: Yup.string().required('Product name is required'),
    description: Yup.string().required('Product description is required'),
    category: Yup.string().required('Product category is required'),
  }),
];

const ProductForm: React.FC = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('');

  const handleSubmit = async (values: FormValues, { setSubmitting }: FormikHelpers<FormValues>) => {
    setIsLoading(true);
    setLoadingMessage('Submitting your product information...');
    try {
      const product = {
        name: values.name,
        description: values.description,
        category: values.category,
      };
      const response = await productApi.createProduct(product);
      if (response.success && response.data) {
        navigate(`/report/${response.data.id}`);
      } else {
        alert(`Error: ${response.error || 'Failed to submit product. Please try again.'}`);
      }
    } catch (error) {
      alert('An unexpected error occurred. Please try again.');
    } finally {
      setIsLoading(false);
      setSubmitting(false);
    }
  };

  return (
    <div className="container">
      <FormContainer>
        <FormHeader>
          <FormTitle>Product Submission Form</FormTitle>
          <FormDescription>
            Complete the form below to generate a transparency report for your product.
          </FormDescription>
        </FormHeader>
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchemas[0]}
          onSubmit={handleSubmit}
          validateOnChange={false}
          validateOnBlur={true}
        >
          {({ isSubmitting, values }) => (
            <Form>
              {initialSteps[0].fields.map((field) => (
                <FormField
                  key={field.id}
                  label={field.question_text}
                  name={field.id || ''}
                  type={field.question_type === 'select' ? 'select' : 'text'}
                  options={field.options}
                  tooltip={field.tooltip}
                  required={field.required}
                />
              ))}
              <FormActions>
                <Button type="submit" disabled={isSubmitting} isLoading={isSubmitting}>
                  Submit
                </Button>
              </FormActions>
            </Form>
          )}
        </Formik>
        {isLoading && (
          <LoadingOverlay>
            <Spinner />
            <p>{loadingMessage}</p>
          </LoadingOverlay>
        )}
      </FormContainer>
    </div>
  );
};

export default ProductForm;
