import axios, { AxiosResponse } from 'axios';
import { 
  Product, 
  Report, 
  ApiResponse, 
  GenerateQuestionsRequest, 
  GenerateQuestionsResponse,
  TransparencyScoreRequest,
  TransparencyScoreResponse
} from '../types';

// Create axios instance with base URL
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000',
  headers: {
    'Content-Type': 'application/json',
  },
});

// AI service instance
const aiService = axios.create({
  baseURL: process.env.REACT_APP_AI_SERVICE_URL || 'http://localhost:8000',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor for API calls
api.interceptors.request.use(
  async (config) => {
    const token = localStorage.getItem('auth_token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for API calls
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;
    if (error.response.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;
      // Handle token refresh or logout logic here
      return api(originalRequest);
    }
    return Promise.reject(error);
  }
);

// Product API calls
export const productApi = {
  createProduct: async (product: Product): Promise<ApiResponse<Product>> => {
    try {
      const response: AxiosResponse<ApiResponse<Product>> = await api.post('/api/products', product);
      return response.data;
    } catch (error: any) {
      return {
        success: false,
        error: error.response?.data?.error || 'Failed to create product',
      };
    }
  },

  getProduct: async (id: string): Promise<ApiResponse<Product>> => {
    try {
      const response: AxiosResponse<ApiResponse<Product>> = await api.get(`/api/products/${id}`);
      return response.data;
    } catch (error: any) {
      return {
        success: false,
        error: error.response?.data?.error || 'Failed to fetch product',
      };
    }
  },
};

// Report API calls
export const reportApi = {
  getReport: async (id: string): Promise<ApiResponse<Report>> => {
    try {
      const response: AxiosResponse<ApiResponse<Report>> = await api.get(`/api/report/${id}`);
      return response.data;
    } catch (error: any) {
      return {
        success: false,
        error: error.response?.data?.error || 'Failed to fetch report',
      };
    }
  },

  downloadReport: async (id: string): Promise<Blob> => {
    try {
      const response = await api.get(`/api/report/${id}/download`, {
        responseType: 'blob',
      });
      return response.data;
    } catch (error: any) {
      throw new Error(error.response?.data?.error || 'Failed to download report');
    }
  },
};

// AI Service API calls
export const aiApi = {
  generateQuestions: async (request: GenerateQuestionsRequest): Promise<ApiResponse<GenerateQuestionsResponse>> => {
    try {
      const response: AxiosResponse<ApiResponse<GenerateQuestionsResponse>> = await aiService.post('/generate-questions', request);
      return response.data;
    } catch (error: any) {
      return {
        success: false,
        error: error.response?.data?.error || 'Failed to generate questions',
      };
    }
  },

  getTransparencyScore: async (request: TransparencyScoreRequest): Promise<ApiResponse<TransparencyScoreResponse>> => {
    try {
      const response: AxiosResponse<ApiResponse<TransparencyScoreResponse>> = await aiService.post('/transparency-score', request);
      return response.data;
    } catch (error: any) {
      return {
        success: false,
        error: error.response?.data?.error || 'Failed to calculate transparency score',
      };
    }
  },
};

export default api;
